import React, { Component } from 'react';
import './App.css';
import Layout from './components/Layout';
import AddTodo from './components/AddTodo';
import uuid from 'uuid';

class App extends Component {

  constructor (props){
    super(props);

    this.state = {
      items:[]
    };
  }

  addItem(inputValue){
    var item={
      id:uuid(),
      comment:inputValue,
      date:new Date()
    };

    this.setState({
      items:items.concat(
        {
          item,
          checked:false
        }
      )
    })
  }

  checkItem(id){
    const finalItems = this.state.items.map((item) => {
      if(item.id === id){
        item.checked =! item.checked
      } 
      return item
    });
    this.setState({
      items: finalItems,
    });
  }

  removeItem(id){
    const finalItems = this.state.items.filter((item) => {
      if(item.id != id) return item
    });
    this.setState({
      todos: finalItems
    });
  }

  render() {
    return (
      <div className="App">
       <Layout>
         <AddTodo />
      </Layout>
      </div>
    );
  }
}

export default App;
